import React, { useEffect, useState } from 'react';
import { useNavigate } from "react-router-dom";
import './Table.css';
import axios from "axios";
import {
  MDBContainer,
  MDBNavbar,
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBCardImage,
} from 'mdb-react-ui-kit';
import Dialog from 'react-bootstrap-dialog';

import { JoinGroupForm } from '../components/JoinGroupForm';
import { ConfirmationModal } from '../components/Modals/ConfirmationModal';
import Popup from '../components/Modals/Popup';
import { JoinChallengePage } from './JoinChallengePage';
import { NavBar } from '../components/NavBar';
interface OverviewProps {
  userName: any;
}
const div = document.createElement("div");
div.style.fontWeight = "bold";
const Overview: React.FunctionComponent<OverviewProps> = () => {
  const [optSmModal, setOptSmModal] = useState(false);
  const [scrollableModal, setScrollableModal] = useState(false);
  const [confirmStatus, setConfirmStatus] = useState();
  const [allChallenges, setAllChallenges] = useState(null);
  const [userDetails, setUserDetails] = useState<any>(null)
  const [post, setPost] = useState<any>();
  const [error, setError] = React.useState(null);
  const [popStatus, setPopUpStatus] = useState<any>(false);
  const [joinChallengeStatus, setJoinChallengeStatus] = useState<any>()
  const navigate = useNavigate();
  const baseURL: any = "http://0.0.0.0:9001"

  function handleJoinChallenge(this: any, id: any) {
    console.log(id);
    if (userDetails.challenge_id) {
      setOptSmModal(!optSmModal);
    }
    if (joinChallengeStatus) {
      axios
        .post(`${baseURL}/join_challenge?username=${sessionStorage.getItem("userName")}&challenge_id=${id}`, {
          title: "Hello World!",
          body: "This is a new post."
        })
        .then((response) => {
          setPost(response);
        });
    }
  }

  console.log(post);

  function setConfirmationStatus(e: any) {
    setConfirmStatus(e);
  }
  function viewGroups() {
    navigate('/groups')
  }

  function logout() {
    navigate('/')
  }


  console.log(sessionStorage.getItem("userName"));

  const handleOK = () => {
    console.log('OK button clicked');
    setPopUpStatus(false);
  };

  const handleCancel = () => {
    console.log('Cancel button clicked');
    setPopUpStatus(false);
  };

  useEffect(() => {
    axios.get(`${baseURL}/all_challenges`).then((response) => {
      setAllChallenges(response.data)
    });

    axios
      .post(`${baseURL}/user?username=${sessionStorage.getItem("userName")}`, {
        title: "Hello World!",
        body: "This is a new post."
      })
      .then((response) => {
        setUserDetails(response.data);
      });
  }, []);

  console.log(userDetails);



  const [basicModal, setBasicModal] = useState(false);

  const toggleShow = () => setOptSmModal(!optSmModal);
  const handleJoinChallengeStatus = () => {
    setJoinChallengeStatus(true)
    setOptSmModal(!optSmModal);
  }
  return (<>
    <NavBar></NavBar>

    <MDBModal show={optSmModal} tabIndex='-1' setShow={setOptSmModal}>
      <MDBModalDialog size='sm'>
        <MDBModalContent>
          <MDBModalHeader>
            <MDBModalTitle>Small modal</MDBModalTitle>
            <MDBBtn className='btn-close' color='none' onClick={toggleShow}></MDBBtn>
          </MDBModalHeader>
          <MDBModalBody>
            {<p>you are already in {userDetails?.challenge_id} do you want to switch challenge..?</p>}
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color='secondary' onClick={toggleShow}>
              Close
            </MDBBtn>
            <MDBBtn onClick={handleJoinChallengeStatus}>switch</MDBBtn>
          </MDBModalFooter>
        </MDBModalContent>
      </MDBModalDialog>
    </MDBModal>

    {/* <MDBModal show={basicModal} setShow={setBasicModal} tabIndex='-1'>
      <MDBModalDialog>
        <MDBModalContent>
          <MDBModalHeader>
            <MDBModalTitle>Modal title</MDBModalTitle>
            <MDBBtn className='btn-close' color='none' onClick={toggleShow}></MDBBtn>
          </MDBModalHeader>
          <MDBModalBody>
            <JoinGroupForm getAllGroups={allgroup} close={toggleShow} confirmationStatus={setConfirmationStatus}></JoinGroupForm>
          </MDBModalBody>
        </MDBModalContent>
      </MDBModalDialog>
    </MDBModal> */}


    {/* <MDBModal show={scrollableModal} setShow={setScrollableModal} tabIndex='-1'>
      <MDBModalDialog scrollable>
        <MDBModalContent>
          <MDBModalHeader>
            <MDBModalTitle>Modal title</MDBModalTitle>
            <MDBBtn
              className='btn-close'
              color='none'
              onClick={() => setScrollableModal(!scrollableModal)}
            ></MDBBtn>
          </MDBModalHeader>
          <MDBModalBody>

            <p> {allgroup && Object.entries(allgroup).map(([key, elem]: any) => (
              <div>
                {elem.group_name}
                {elem.members.map((member: any) => (
                  <div>{member}</div>
                ))}
                <div><MDBBtn rounded onClick={() => joinGroup(elem, key)}>Join Groups</MDBBtn>
                </div>
              </div>

            ))}</p>

          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn color='secondary' onClick={() => setScrollableModal(!setScrollableModal)}>
              Close
            </MDBBtn>
          </MDBModalFooter>
        </MDBModalContent>
      </MDBModalDialog>
    </MDBModal> */}




    {/* {confirmStatus && <ConfirmationModal confirmationStatus={confirmStatus}></ConfirmationModal>
    } */}

    {/* {popStatus && (
      <Popup
        message="Are you sure you want to do this?"
        onOK={handleOK}
        onCancel={handleCancel}
      />
    )} */}


    {/* -------------------------------------------------------------------------------------- */}
    {/* <MDBNavbar expand='lg' light bgColor='light'>
            <MDBContainer fluid>
                <MDBBtn rounded onClick={logout}>Log out</MDBBtn>

            </MDBContainer>
        </MDBNavbar> */}
    <div>
      <table>
        <thead>
          <tr>
            <th>User Details</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>UserName - {sessionStorage.getItem("userName")}</td>
          </tr>
          <tr>
            <td>User Email Address - {sessionStorage.getItem("mail")}</td>
          </tr>
          <tr>
            <td>current Step count - </td>
          </tr>
        </tbody>
      </table>
    </div>


    <div>
      <table>
        <thead>
          <tr>
            <th>Participation</th>
          </tr>
        </thead>
      </table>
    </div>

    <div>
      <table>
        <thead>
          <tr>
            <th>Challenge Name</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Steps</td>
          </tr>
          <tr>
            <td>Step Count</td>
          </tr>
        </tbody>
      </table>
    </div>

    <div>
      <table>
        <thead>
          <tr>
            <th>On Going Challenges</th>
          </tr>
        </thead>
      </table>
    </div>

    <div>
      <MDBCard>
        <MDBCardBody>
          <MDBCardTitle>Join Challenge</MDBCardTitle>
          <MDBCardText>
            {allChallenges && Object.values(allChallenges).map((elem: any) => (
              <div key={elem.id}>
                <p>{elem.name}</p>
                <div>
                  <MDBBtn rounded onClick={() => handleJoinChallenge(elem.id)}>join Challenge</MDBBtn>
                </div>
              </div>
            ))}

          </MDBCardText>
        </MDBCardBody>
      </MDBCard>
    </div>

  </>)
}
export default Overview;
