          <MDBModalFooter>
            <MDBBtn color='secondary' onClick={toggleShowLeaderBoard}>
              Close
            </MDBBtn>
          </MDBModalFooter>
        </MDBModalContent>
      </MDBModalDialog>
    </MDBModal>}






    <div className="text-center">
      <table>
        <thead>
          <tr>
